mapper.select:
select a_pk, m_pk_writer, a_title, a_startprice, a_endprice, c_category, a_regDate, a_starttime, a_endtime, a_readcount, a_comment, r_pk, a_subscribe , a_currentprice, ag_regdate
from AUCTION a JOIN CATEGORYNAME c ON (a.c_pk = c.c_pk),
( select ag_bidding as a_currentprice, ag_regdate from (select /*+ INDEX (ag AG_BIDDING_INDEX)*/ rownum rn, ag_bidding ,ag_regdate from AUCTION_GRAPH ag where a_pk = #{a_pk}) where rn = 1 )
where a_pk = #{a_pk}
