mapper.select:
select a_pk, m_pk_writer, a_title, a_startprice, a_endprice, c_category, a_regDate, a_starttime, a_end, a_start, a_endTime, a_readcount, a_comment , a_currentprice, to_char(ag_regdate,'yyyy/MM/dd HH:mm') as ag_regdate, a_country, a_size, a_color,a_details, au.at_name, (select count(*) from member_subscribe where ms_type = 3 and ms_fk = #{a_pk}) as a_subscribe from AUCTION a JOIN CATEGORYNAME c ON (a.c_pk = c.c_pk) join AUTHOR au on (a.m_pk_writer = au.m_pk ) join HANDUS_IMAGE hi on (hi.hi_fk = a.a_pk and hi.hi_main = 1 and hi.hi_type = 2),
( select ag_bidding as a_currentprice, ag_regdate from (select /*+ INDEX (ag AG_BIDDING_INDEX)*/ rownum rn, ag_bidding ,ag_regdate from AUCTION_GRAPH ag where a_pk = #{a_pk}) where rn = 1 )
where a_pk = #{a_pk}
