mapper.update:
update ITEM 
set I_TITLE = #{i_title}, I_PRICE = #{i_price}, I_UPDATEDATE = SYSDATE, I_STOCK = #{i_stock}, 
	I_CONTENT = #{i_content}, I_COMMENT = #{i_comment}, I_COUNTRY = #{i_country}, I_COLOR = #{i_color}, I_SIZE = #{i_size} 
where I_PK = #{i_pk}

update ITEM 
set I_READ_COUNT = I_READ_COUNT + 1
where I_PK = #{i_pk}

update ITEM 
